mapper.update:
UPDATE courses
SET course_name = #{courseName}, credits = #{credits}, department = #{department}, description = #{description}, is_active = #{isActive}
WHERE id = #{id}

mapper.select:
SELECT *
FROM courses


        (course_name LIKE CONCAT('%', #{keyword}, '%') OR course_code LIKE CONCAT('%', #{keyword}, '%'))


        AND department = #{department}


        AND is_active = #{isActive}


ORDER BY created_at DESC
LIMIT #{pageSize} OFFSET #{offset}

SELECT COUNT(1)
FROM courses


        (course_name LIKE CONCAT('%', #{keyword}, '%') OR course_code LIKE CONCAT('%', #{keyword}, '%'))


        AND department = #{department}


        AND is_active = #{isActive}
